import React, { useState, useEffect } from 'react';
import movies from './movies';
import MoviePoster from './MoviePoster';
import './TournamentStyles.css';


const Tournament = () => {
    const [currentRound, setCurrentRound] = useState([]);
    const [matchupIndex, setMatchupIndex] = useState(0);
    const [selectedMovies, setSelectedMovies] = useState([]);
    const [winner, setWinner] = useState('');

    useEffect(() => {
        const shuffledMovies = [...movies].sort(() => 0.5 - Math.random());
        setCurrentRound(shuffledMovies);
        window.addEventListener('keydown', handleKeyPress);
        return () => {
            window.removeEventListener('keydown', handleKeyPress);
        };
    }, []);

    const handleKeyPress = (event) => {
        if (event.key === 'ArrowRight') {
            selectMovie(currentRound[matchupIndex]);
        } else if (event.key === 'ArrowLeft') {
            selectMovie(currentRound[matchupIndex + 1]);
        }
    };

    const selectMovie = (movie) => {
        const nextIndex = matchupIndex + 2;
        const updatedSelected = [...selectedMovies, movie];

        setSelectedMovies(updatedSelected);
        setMatchupIndex(nextIndex);

        if (nextIndex >= currentRound.length) {
            if (currentRound.length === 2) {
                setWinner(movie);
            } else {
                setCurrentRound(updatedSelected);
                setSelectedMovies([]);
                setMatchupIndex(0);
            }
        }
    };

    const goBack = () => {
        if (matchupIndex > 0) {
            const prevIndex = matchupIndex - 2;
            setSelectedMovies(selectedMovies.slice(0, -1));
            setMatchupIndex(prevIndex);
        }
    };

    return (
        <div>
            <h2>Tournament Mode</h2>
            {winner ? (
                <div>
                    <h3>Winner:</h3>
                    <MoviePoster title={winner} />
                </div>
            ) : (
                <div>
                    {currentRound.length > 0 && matchupIndex < currentRound.length && (
                        <div>
                            <div onClick={() => selectMovie(currentRound[matchupIndex])}>
                                <MoviePoster title={currentRound[matchupIndex]} />
                            </div>
                            <div onClick={() => selectMovie(currentRound[matchupIndex + 1])}>
                                <MoviePoster title={currentRound[matchupIndex + 1]} />
                            </div>
                        </div>
                    )}
                    {matchupIndex > 0 && <button onClick={goBack}>Go Back</button>}
                </div>
            )}
        </div>
    );
};

export default Tournament;
