import React, { useState, useEffect } from 'react';
import movies from './movies'; // Make sure to import your list of movies

const Tournament = () => {
    const [currentRound, setCurrentRound] = useState([]);
    const [nextRound, setNextRound] = useState([]);
    const [winner, setWinner] = useState('');

    useEffect(() => {
        // Shuffle and set the initial round
        const shuffledMovies = [...movies].sort(() => 0.5 - Math.random());
        setCurrentRound(shuffledMovies);
    }, []);

    const handleSelection = (movie) => {
        // Add the selected movie to the next round
        setNextRound(prev => [...prev, movie]);

        // If the current round is complete, prepare the next one
        if (nextRound.length === Math.ceil(currentRound.length / 2) - 1) {
            if (currentRound.length === 2) {
                // If the final round, declare the winner
                setWinner(movie);
            } else {
                // Set up the next round
                setCurrentRound(nextRound);
                setNextRound([]);
            }
        }
    };

    return (
        <div>
            <h2>Tournament Mode</h2>
            {winner ? (
                <div>
                    <h3>Winner: {winner}</h3>
                </div>
            ) : (
                <div>
                    {currentRound.map((movie, index) => (
                        index % 2 === 0 && (
                            <div key={index}>
                                <button onClick={() => handleSelection(movie)}>{movie}</button>
                                {' vs '}
                                <button onClick={() => handleSelection(currentRound[index + 1])}>{currentRound[index + 1]}</button>
                            </div>
                        )
                    ))}
                </div>
            )}
        </div>
    );
};

export default Tournament;
