import React, { useState, useEffect } from 'react';
import movies from './movies';
import MoviePoster from './MoviePoster';
import './TournamentStyles.css';

const Tournament = () => {
    const [currentRound, setCurrentRound] = useState([]);
    const [nextRound, setNextRound] = useState([]);
    const [matchupIndex, setMatchupIndex] = useState(0);
    const [winner, setWinner] = useState('');

    const totalMovies = movies.length;
    const previousPowerOf2 = Math.pow(2, Math.floor(Math.log2(totalMovies)));
    const difference = totalMovies - previousPowerOf2;
    const initialMatchupsCount = difference * 2;

    useEffect(() => {
        const shuffledMovies = [...movies].sort(() => 0.5 - Math.random());

        let initialMatchups = [];
        let automaticWinners = shuffledMovies.slice(initialMatchupsCount);

        for (let i = 0; i < initialMatchupsCount; i += 2) {
            initialMatchups.push([shuffledMovies[i], shuffledMovies[i + 1]]);
        }

        setCurrentRound(initialMatchups);
        setNextRound(automaticWinners);
    }, []);

    const selectMovie = (movie) => {
        setNextRound([...nextRound, movie]);

        if (nextRound.length + 1 >= currentRound.length) {
            if (nextRound.length + 1 === 1) {
                setWinner(movie);
            } else {
                let newRound = [];
                for (let i = 0; i < nextRound.length; i += 2) {
                    newRound.push([nextRound[i], nextRound[i + 1]]);
                }
                setCurrentRound(newRound);
                setNextRound([]);
                setMatchupIndex(0);
            }
        } else {
            setMatchupIndex(matchupIndex + 1);
        }
    };

    const goBack = () => {
        if (matchupIndex > 0) {
            setNextRound(nextRound.slice(0, -1));
            setMatchupIndex(matchupIndex - 1);
        }
    };


    const getRoundName = (numMovies) => {
        if (numMovies <= 2) return 'Final';
        if (numMovies <= 4) return 'Semi Finals';
        if (numMovies <= 8) return 'Quarter Finals';
        if (numMovies <= 16) return 'Round of 16';
        if (numMovies <= 32) return 'Round of 32';
        if (numMovies <= 64) return 'Round of 64';
        if (numMovies <= 128) return 'Round of 128';
        if (numMovies <= 256) return 'Round of 256';
        if (numMovies <= 512) return 'Round of 512';
        if (numMovies <= 1024) return 'Round of 1024';
        return '';
    };

    const currentMatchupNumber = matchupIndex + 1;
    const totalMatchups = currentRound.length + byes;
    const roundName = getRoundName(currentRound.length * 2 + nextRound.length);

    return (
        <div>
            <h2>Tournament Mode</h2>
            {!winner && (
                <div>
                    <p>Current Matchup: {currentMatchupNumber} out of {totalMatchups}</p>
                    <p>Current Round: {roundName}</p>
                </div>
            )}
            {winner ? (
                <div>
                    <h3>Winner:</h3>
                    <MoviePoster title={winner} />
                </div>
            ) : (
                <div>
                    {currentRound.length > 0 && currentRound[matchupIndex] && (
                        <div className="matchup">
                            {currentRound[matchupIndex].map((movie, index) => (
                                <div key={index} className="card" onClick={() => selectMovie(movie)}>
                                    <MoviePoster title={movie} />
                                </div>
                            ))}
                        </div>
                    )}
                    {matchupIndex > 0 && <button onClick={goBack}>Go Back</button>}
                </div>
            )}
        </div>
    );
};

export default Tournament;
