import React, { useState, useEffect } from 'react';
import movies from './movies';
import MoviePoster from './MoviePoster';
import './TournamentStyles.css';

const Tournament = () => {
    const [currentRound, setCurrentRound] = useState([]);
    const [nextRound, setNextRound] = useState([]);
    const [matchupIndex, setMatchupIndex] = useState(0); // Ensure this is defined
    const [winner, setWinner] = useState('');

    const totalMovies = movies.length;
    const nextPowerOf2 = Math.pow(2, Math.ceil(Math.log2(totalMovies)));
    const byes = nextPowerOf2 - totalMovies;

    useEffect(() => {
        const shuffledMovies = [...movies].sort(() => 0.5 - Math.random());
        const moviesInRound = shuffledMovies.slice(byes);
        setCurrentRound(moviesInRound);
        setNextRound(shuffledMovies.slice(0, byes));

        const handleKeyPress = (event) => {
            if (event.key === 'ArrowRight' && nextRound.length < currentRound.length - 1) {
                selectMovie(currentRound[nextRound.length]);
            } else if (event.key === 'ArrowLeft' && nextRound.length < currentRound.length - 1) {
                selectMovie(currentRound[nextRound.length + 1]);
            }
        };

        window.addEventListener('keydown', handleKeyPress);
        return () => {
            window.removeEventListener('keydown', handleKeyPress);
        };
    }, []);

    const selectMovie = (movie) => {
        const updatedNextRound = [...nextRound, movie];
        setNextRound(updatedNextRound);

        if (updatedNextRound.length >= currentRound.length / 2) {
            if (currentRound.length === 2) {
                setWinner(movie);
            } else {
                setCurrentRound(updatedNextRound.slice(byes));
                setNextRound([]);
                setMatchupIndex(0);
            }
        } else {
            setMatchupIndex(matchupIndex + 2);
        }
    };

    const handleDelete = (deletedTitle) => {
        setCurrentRound(currentRound.filter(movie => movie !== deletedTitle));
        setNextRound(nextRound.filter(movie => movie !== deletedTitle));
    };

    const goBack = () => {
        if (matchupIndex > 0) {
            setNextRound(nextRound.slice(0, -1));
            setMatchupIndex(matchupIndex - 2);
        }
    };


    const getRoundName = (numMovies) => {
        if (numMovies <= 2) return 'Final';
        if (numMovies <= 4) return 'Semi Finals';
        if (numMovies <= 8) return 'Quarter Finals';
        if (numMovies <= 16) return 'Round of 16';
        if (numMovies <= 32) return 'Round of 32';
        if (numMovies <= 64) return 'Round of 64';
        if (numMovies <= 128) return 'Round of 128';
        if (numMovies <= 256) return 'Round of 256';
        if (numMovies <= 512) return 'Round of 512';
        if (numMovies <= 1024) return 'Round of 1024';
        return '';
    };

    const currentMatchupNumber = Math.floor(matchupIndex / 2) + 1;
    const totalMatchups = Math.floor(currentRound.length / 2) + byes;
    const roundName = getRoundName(currentRound.length + nextRound.length - byes);

    return (
        <div>
            <h2>Tournament Mode</h2>
            {!winner && (
                <div>
                    <p>Current Matchup: {currentMatchupNumber} out of {totalMatchups}</p>
                    <p>Current Round: {roundName}</p>
                </div>
            )}
            {winner ? (
                <div>
                    <h3>Winner:</h3>
                    <MoviePoster title={winner} />
                </div>
            ) : (
                <div>
                    {currentRound.length > 0 && matchupIndex < currentRound.length && (
                        <div className="matchup">
                            <div className="card" onClick={() => selectMovie(currentRound[matchupIndex])}>
                                <MoviePoster title={currentRound[matchupIndex]} />
                            </div>
                            <div className="card" onClick={() => selectMovie(currentRound[matchupIndex + 1])}>
                                <MoviePoster title={currentRound[matchupIndex + 1]} />
                            </div>
                        </div>
                    )}
                    {matchupIndex > 0 && <button onClick={goBack}>Go Back</button>}
                </div>
            )}
        </div>
    );
};

export default Tournament;
