import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MoviePoster = ({ title }) => {
    const [posterUrl, setPosterUrl] = useState('');

    useEffect(() => {
        axios.get(`http://www.omdbapi.com/?t=${encodeURIComponent(title)}&apikey=abe82a9d`)
            .then(response => {
                setPosterUrl(response.data.Poster);
            })
            .catch(error => {
                console.error('Error fetching data: ', error);
            });
    }, [title]);

    return (
        <div>
            <h3>{title}</h3>
            {posterUrl ? <img src={posterUrl} alt={`Poster of ${title}`} /> : <p>Loading...</p>}
        </div>
    );
};

export default MoviePoster;
